SELECT e.id AS e_id, e.buildingId,e.elevatorModel,e.elevatorNumber,e.internalNumber,e.elevatorType, e.usingState,e.firstMaintenance,e.maintenanceId,e.maintenancePersonId,e.elevatorCode,e.registrationCode, e.factoryNumber,e.usingNumber,e.registrationStatus,e.proactivelyDate,e.creater,e.createDate,e.updater,e.updatDate,e.projectId, (SELECT companyName FROM building WHERE id=e.buildingId) AS companyName,(SELECT d.valueName FROM dictionary d WHERE d.typeCode='elevatorType'AND d.valueId=e.elevatorType)AS valueName ,(SELECT d.valueName FROM dictionary d WHERE d.typeCode='usingState' AND d.valueId=e.usingState)AS valueName1 FROM USER u,elevatorinfo e WHERE e.maintenancePersonId=u.id AND u.userRole=#{userRole} and u.id=#{id}
and e.buildingId = #{buildingId}
and Company_YId = #{Company_YId}
and maintenanceId = #{maintenanceId}
and elevatorType = #{elevatorType}
and elevatorCode like CONCAT ('%',#{elevatorCode},'%')
and registrationCode like CONCAT ('%',#{registrationCode},'%')
and registrationStatus = #{registrationStatus}
and usingState = #{usingState}
ORDER BY e.id ASC LIMIT #{from},#{pageSize}

	
	
